import "./style.css";
import { Link } from 'react-router-dom';
import React, { useState } from 'react';
import Modal from 'react-modal';
import './style.css';

Modal.setAppElement('#root');


const Header = () => {

		const [modalIsOpen, setModalIsOpen] = useState(false);
		const [name, setName] = useState('');
		const [contactMethod, setContactMethod] = useState('');
		const [message, setMessage] = useState('');

		const openModal = () => {
		  setModalIsOpen(true);
		};

		const closeModal = () => {
		  setModalIsOpen(false);
		};

		const handleSubmit = (e) => {
		  e.preventDefault();
		  // Здесь можно обработать данные формы, например, отправить их на сервер
		  console.log('Имя:', name);
		  console.log('Способ связи:', contactMethod);
		  console.log('Сообщение:', message);
		  closeModal();
		};
    return (

		<header className="header">
			<div className="header__wrapper">
				<h1 className="header__title">
					<strong>
						Привет, я <em>Елена</em>
					</strong>
					<br />психотерапевт
				</h1>
				<div className="header__text">
					<p>с большим опытом и знаниями.</p>
				</div>
				<Modal
        isOpen={modalIsOpen}
        onRequestClose={closeModal}
        className="modal"
        overlayClassName="overlay"
        contentLabel="Форма записи"
      >
        <h2 className="title-2">Записаться к психотерапевту</h2>
        <form onSubmit={handleSubmit} className="form">
          <div className="form-group">
            <p>Имя:</p>
            <input
              type="text"
              value={name}
              onChange={(e) => setName(e.target.value)}
              required
            />
          </div>
          <div className="form-group">
            <p>Ваши контакты</p>
            <input
              type="text"
              value={contactMethod}
              onChange={(e) => setContactMethod(e.target.value)}
              required
            />

          </div>
          <div className="form-group">
            <p>Сообщение:</p>
            <textarea
              value={message}
              onChange={(e) => setMessage(e.target.value)}
              required
            ></textarea>
          </div>
          <div className="form-buttons">
            <button type="submit" className="submit-btn">
              Отправить
            </button>
            <button type="button" onClick={closeModal} className="close-btn">
              Закрыть
            </button>
          </div>
        </form>
      </Modal>
	  			<a className="btn" onClick={openModal}>Попасть ко мне</a>

			</div>
		</header>
	);
}

export default Header;
