import React, { useState } from 'react'
import { useNavigate, Link} from 'react-router-dom'

import "./style.css"

const Login = (props) => {
  const [email, setEmail] = useState('')
  const [password, setPassword] = useState('')
  const [emailError, setEmailError] = useState('')
  const [passwordError, setPasswordError] = useState('')

  const navigate = useNavigate()

  const onButtonClick = () => {
    // Set initial error values to empty
    setEmailError('')
    setPasswordError('')
  
    // Check if the user has entered both fields correctly
    if ('' === email) {
      setEmailError('Введите почту')
      return
    }
  
    if (!/^[\w-\.]+@([\w-]+\.)+[\w-]{2,4}$/.test(email)) {
      setEmailError('Ошибка, введите правильный адрес почты')
      return
    }
  
    if ('' === password) {
      setPasswordError('Введите пароль')
      return
    }
  
    if (password.length < 7) {
      setPasswordError('Пароль должен быть не меньше 8 символов')
      return
    }
  
    // Authentication calls will be made here...
  }

  return (
    <div className={'mainContainer'}>
        <h1 className="title-1">Вход</h1>
      <div className={'inputContainer'}>
        <input
          value={email}
          placeholder="почта"
          onChange={(ev) => setEmail(ev.target.value)}
          className={'inputBox'}
        />
        <label className="errorLabel">{emailError}</label>
      </div>
      <br />
      <div className={'inputContainer'}>
        <input
          value={password}
          placeholder="пароль"
          onChange={(ev) => setPassword(ev.target.value)}
          className={'inputBox'}
        />
        <label className="errorLabel">{passwordError}</label>
      </div>
      <br />
      <div className={'inputContainer'}>
        <input className={'login-btn'} type="button" onClick={onButtonClick} value={'Войти'} />
      </div>
      <div className='container'>
      <Link to="/register" className='registerLogin'>
                Нет аккаунта?
            </Link>
            <p>
							<a href="#!">Санкт-Петербург,<br />Трамвайный проспект, дом 12, корпус 2</a>
						</p>
        <div>
      </div>
          
      </div>
        
    </div>
  )
}

export default Login
